import { BrowserRouter, Link, Route, Switch } from 'react-router-dom'
const addproduct=()=>{
    const[pname,setProductName]=useState('');
    const[pmaingrp ,setProductMainGrp]=useState('');
    const[psubgrp ,setProductSubGrp]=useState('');
    const[ptype,setProductType]=useState('');
    const[pbrand,setProductBrand]=useState('');
    const[currentstock ,setCurrentStock]=useState(''); 
    const[minstock ,setMinStock]=useState('');
    const[maxstock ,setMaxStock]=useState('');
    const[reorderlevel  ,setReorderLevel]=useState('');



    return (
        <div>
        <h1 className="page-title">Add Product</h1>
  
        <div className="mb-3">
          <label htmlFor="">Product Name</label>
          <input
            onChange={(e) => {
              setProductName(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="">Product Main grp</label>
          <input
            onChange={(e) => {
              setProductMainGrp(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="">Product Sub grp</label>
          <input
            onChange={(e) => {
              setProductSubGrp(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="">Product Type</label>
          <input
            onChange={(e) => {
              setProductType(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>
            
        <div className="mb-3">
          <label htmlFor="">Product Brand</label>
          <input
            onChange={(e) => {
              setProductBrand(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="">Product Current Stock</label>
          <input
            onChange={(e) => {
              setCurrentStock(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="">Product Min Stock</label>
          <input
            onChange={(e) => {
              setMinStock(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="">Product Max Stock</label>
          <input
            onChange={(e) => {
                setMaxStock(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>
        
        <div className="mb-3">
          <label htmlFor="">Product Reorder Level</label>
          <input
            onChange={(e) => {
                setReorderLevel(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="">Product Reorder Quantity</label>
          <input
            onChange={(e) => {
                setReorderQuantity(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="">Product Sell Price</label>
          <input
            onChange={(e) => {
                setSellPrice(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="">Alert Message</label>
          <input
            onChange={(e) => {
                setalert(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="">Thumbail</label>
          <input
            accept="image/*"
            onChange={onFileSelect}
            type="file"
            className="form-control"
          />
        </div>
  
        <div className="mb-3">
          <button onClick={addproductToDB} className="btn btn-success">
            Add
          </button>
  
          <Link to="/product">
            <a className="btn btn-warning">Back</a>
          </Link>
        </div>
      </div>

    )
}

export default addproduct;
