import { useState } from 'react';
import {  Link ,useHistory} from 'react-router-dom'
import axios from "axios"
import { url } from "../common/constants";
const Addproduct=()=>{
    const[pname,setProductName]=useState('')
    const[pimage,setProductImage]=useState(undefined)
    const[pmaingrp ,setProductMainGrp]=useState('')
    const[psubgrp ,setProductSubGrp]=useState('')
    const[ptype,setProductType]=useState('')
    const[pbrand,setProductBrand]=useState('')
    const[currentstock ,setCurrentStock]=useState('')
    const[minstock ,setMinStock]=useState('')
    const[maxstock ,setMaxStock]=useState('')
    const[reorderlevel  ,setReorderLevel]=useState('')
    const[reorderquantity  ,setReorderQuantity]=useState('')
    const[sellprice,setSellPrice]=useState('')
    const[alertmsg,setAlertmsg]=useState('')

    const history = useHistory()

    const onFileSelect = (event) => {
      setProductImage(event.target.files[0])
    }

    const addproductToDB=()=>{
        const data={pname,pimage,pmaingrp,psubgrp,ptype,pbrand,currentstock,minstock,maxstock,reorderlevel,reorderquantity,sellprice,alertmsg};

        axios.post(url + '/product/save ', data).then((response) => {
            const result = response.data
            console.log(result);
            alert("Product Added successfully");
            history.push('/product')
          })
    }

    return (
        <div>
        <h1 className="page-title">Add Product</h1>
  
        <div className="mb-3">
          <label htmlFor="">Product Name</label>
          <input
            onChange={(e) => {
              setProductName(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
        <label htmlFor="">Product Image</label>
        <input
          accept="image/*"
          onChange={onFileSelect}
          type="file"
          className="form-control"
        />
          </div>

        <div className="mb-3">
          <label htmlFor="">Product Main grp</label>
          <input
            onChange={(e) => {
              setProductMainGrp(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="">Product Sub grp</label>
          <input
            onChange={(e) => {
              setProductSubGrp(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="">Product Type</label>
          <input
            onChange={(e) => {
              setProductType(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>
            
        <div className="mb-3">
          <label htmlFor="">Product Brand</label>
          <input
            onChange={(e) => {
              setProductBrand(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="">Product Current Stock</label>
          <input
            onChange={(e) => {
              setCurrentStock(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="">Product Min Stock</label>
          <input
            onChange={(e) => {
              setMinStock(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="">Product Max Stock</label>
          <input
            onChange={(e) => {
                setMaxStock(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>
        
        <div className="mb-3">
          <label htmlFor="">Product Reorder Level</label>
          <input
            onChange={(e) => {
                setReorderLevel(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="">Product Reorder Quantity</label>
          <input
            onChange={(e) => {
                setReorderQuantity(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="">Product Sell Price</label>
          <input
            onChange={(e) => {
                setSellPrice(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="">Alert Message</label>
          <input
            onChange={(e) => {
                setAlertmsg(e.target.value)
            }}
            type="text"
            className="form-control"
          />
        </div>

        <div className="mb-3">
          <button onClick={addproductToDB} className="btn btn-success">
            Add
          </button>

          <Link to="/product">
          <button className="btn btn-warning">Back</button>
          </Link>
        </div>
      </div>

    )
}

export default Addproduct;
